import './App.css';
import React, {useState,useEffect} from 'react';

export const Favoritos =( {children} ) =>{
            const [personajesfa, setPersonajefa] = useState(()=>{
                 try{
                   const personajesfaLocal =localStorage.getItem("personajes");
                   return personajesfaLocal ? JSON.parse(personajesfaLocal):[];
                 } catch(error){
                   return[];
                 }
            });
        useEffect(()=>{
              localStorage.setItem('personajes', JSON.stringify(personajesfa))
        },[personajesfa]) 

       /**Agregar personaje a favoritos */ 
  const AddPersonaje = ( personaje) => {
  const inFav = personajesfa.find(
  (personajeinFav) => personajeinFav.id === personaje.id
  );
  if(inFav){
    setPersonajefa(
      personajesfa.map((personajeinFav)=>{
       return {...inFav, amount: inFav.amount +1 } 
      })
    )
  }
}
}
